INSERT INTO crawler.product_review (id, review_id, source, product_id, product_name, sku_id, rating, title, comment, nickname, helpful_votes, not_helpful_votes, helpful_score, is_deleted, created_at, updated_at) VALUES (5365, '477016025', 'gap', '728822', null, null, 5.0, 'Fit Small, But Great Quality', 'I\'m usually a 27 in Gap denim (proportions are 32-28-38), and 28 in other brands. I\'m very tall, but not really curvy. Bought the 28 in these jeans based on other reviews saying they fit small. They absolutely do. I would size up at least one size and if you normally wear very stretchy denim, you may not like these that much. That said, as a tall woman, the tall was a GREAT length and the quality seems better than other jeans from Gap. So 5 stars for me.', 'Claire S', 0, 0, 2000.0, 0, '2024-06-12 18:01:41', '2024-06-12 18:01:41');
