INSERT INTO crawler.product_review (id, review_id, source, product_id, product_name, sku_id, rating, title, comment, nickname, helpful_votes, not_helpful_votes, helpful_score, is_deleted, created_at, updated_at) VALUES (5367, '476821505', 'gap', '728822', null, null, 3.0, 'Strange fit', 'I agree with other reviews that it\'s worth sizing up for a relaxed fit through the legs.  I\'m 5\'11 and usually a 31 in non-stretch denim and I got these in a 33 tall to heed both recommendations from the reviews and from the description.  They fit just as I\'d hoped in the legs, relaxed and flattering, but the waist feels true to size for a 33 and is way too big on me.  I haven\'t decided yet whether to return them or have them altered since I like the leg fit so much but thought it was worth a review for others considering sizing up.', 'Kelly P.', 2, 0, 2046.0, 0, '2024-06-12 18:01:41', '2024-06-12 18:01:41');
INSERT INTO crawler.product_review (id, review_id, source, product_id, product_name, sku_id, rating, title, comment, nickname, helpful_votes, not_helpful_votes, helpful_score, is_deleted, created_at, updated_at) VALUES (5368, '476673167', 'gap', '728822', null, null, 3.0, 'What is with the sizing?', 'I am 6/28 in most of my gap jeans. I needed a 2/26 in these. Purchased in store. When I got home, I measured them against other GAP jeans and they measure exactly the same as my Vintage High Rise size 6. Same waist and rise. So my size 2/26 measures 14" (which is a 28). Other reviewers said they run big and many found they run small and had to size up. Regular length hits top of foot for me at 5\'9" (and measures 31"). Please start including the ACTUAL sizes for different styles on your size chart. Denim is good quality though.', 'Sarah', 1, 0, 1971.0, 0, '2024-06-12 18:01:41', '2024-06-12 18:01:41');
INSERT INTO crawler.product_review (id, review_id, source, product_id, product_name, sku_id, rating, title, comment, nickname, helpful_votes, not_helpful_votes, helpful_score, is_deleted, created_at, updated_at) VALUES (5369, '476632495', 'gap', '728822', null, null, 3.0, 'Huge Variation in Fit - Possibly Sized Wrong?', 'I\'ve had four pairs of these 90\'s Straight High Rise jeans, and the dark indigo ones are my absolute favorite jeans. I ordered these, thinking I\'d have a black pair that fit just like those. Nope! These literally will not fit on my body. Won\'t even come close to buttoning. The others (medium and dark washes, all the same size and style) are comfortably loose and perfect. I have no idea what\'s up with these, or if I just got an odd pair. My recommendation is to buy at least two identical pairs and compare them, because they\'re not all the same.', 'Lauren B.', 2, 0, 2052.0, 0, '2024-06-12 18:01:41', '2024-06-12 18:01:41');
